class MX_Controller 
{
	public $autoload = array();
	
	public function __construct() 
	{
		$class = str_replace(CI::$APP->config->item('controller_suffix'), '', get_class($this));
		log_message('debug', $class." MX_Controller Initialized");
		Modules::$registry[strtolower($class)] = $this;	
		
		/* copy a loader instance and initialize */
		$this->load = clone load_class('Loader');
		$this->load->initialize($this);	
		
		/* autoload module items */
		$this->load->_autoloader($this->autoload);
		$this->output->enable_profiler(TRUE);

		/* custom user auth for backend */
		$this->load->model('user/Adminauth_model');
		$this->preDispatch();
	}
	
	public function __get($class) 
	{
		return CI::$APP->$class;
	}

	public function preDispatch()
	{
		if (!$this->Adminauth_model->isLoggedIn()) {
            redirect('user/auth/login');
        }
	}

	public function redirectIndex()
	{
		$module = $this->uri->segment('1');
		$controller = $this->uri->segment('2');
		redirect($module . '/' . $controller);
	}

	public function initPagination($total, $limit = 20)
	{
		$module = $this->uri->segment('1');
		$controller = $this->uri->segment('2');
		$this->load->library('pagination');
		$config['base_url'] = base_url($module . '/' . $controller);
		$config['total_rows'] = $total;
		$config['per_page'] = $limit;
		$config['page_query_string'] = TRUE;

		$config['full_tag_open'] = '<ul class="pagination">';
		$config['full_tag_close'] = '</ul>';
		$config['first_tag_open'] = '<li>';
		$config['first_tag_close'] = '</li>';
		$config['last_tag_open'] = '<li>';
		$config['last_tag_close'] = '</li>';
		$config['next_tag_open'] = '<li>';
		$config['next_tag_close'] = '</li>';
		$config['prev_tag_open'] = '<li>';
		$config['prev_tag_close'] = '</li>';
		$config['cur_tag_open'] = '<li class="page-item active"><a class="page-link">';
		$config['cur_tag_close'] = '</a></li>';
		$config['num_tag_open'] = '<li>';
		$config['num_tag_close'] = '</li>';
		$config['attributes'] = array('class' => 'page-link');

		$this->pagination->initialize($config);
		return $this->pagination;
	}
}